
'use client';

// A short, public domain click sound, Base64 encoded.
const clickSoundData =
  'data:audio/mpeg;base64,';

let audio: HTMLAudioElement | null = null;

const createAudio = () => {
  if (typeof window !== 'undefined' && !audio) {
    audio = new Audio(clickSoundData);
    audio.preload = 'auto';
  }
};

// Pre-create the audio element when the module is loaded on the client
createAudio();

export const playClickSound = () => {
  if (audio) {
    audio.currentTime = 0; // Rewind to the start
    audio.play().catch(error => {
      // Autoplay was prevented. Common in browsers until user interaction.
      // We can ignore this error silently for subsequent UI sounds.
    });
  }
};
